mapper.select:
SELECT ai.admin_info_id,ai.admin_id,ai.the_department,ai.full_name,ai.staff_number,ai.mail_box,ai.mobile_phone,ai.home_address FROM admin_info ai,department_admin da WHERE ai.admin_id = da.admin_id
AND da.d_id = #{parameters.did}
AND ai.full_name LIKE concat(concat('%',#{parameters.fullName}),'%')
AND ai.state = '1' limit #{minRow},#{pageSize}

SELECT ai.admin_info_id,ai.admin_id,ai.the_department,ai.full_name,ai.staff_number,ai.mail_box,ai.mobile_phone,ai.home_address FROM admin_info ai LEFT JOIN department_admin da ON ai.admin_id = da.admin_id WHERE ai.state = '1'
AND da.id LIKE concat(concat('%',#{parameter}),'%') OR ai.full_name LIKE concat(concat('%',#{parameter}),'%')
limit #{minRow},#{pageSize}

SELECT ai.admin_info_id,ai.admin_id ,ai.birth_date ,ai.full_name,ai.id_card_no,ai.staff_number,ai.birth_date,ai.gender,ai.mobile_phone,ai.the_department,a.admin_state FROM admin_info ai LEFT JOIN admin a ON ai.admin_id = a.id WHERE ai.state = '1'
AND ai.the_department = #{parameters.theDepartment}
AND ( ai.full_name LIKE concat(concat('%',#{parameters.vague}),'%') OR ai.staff_number LIKE concat(concat('%',#{parameters.vague}),'%') )
limit #{minRow},#{pageSize}

SELECT COUNT(ai.admin_info_id) FROM admin_info ai LEFT JOIN admin a ON ai.admin_id = a.id WHERE ai.state = '1'
AND ai.the_department = #{parameters.theDepartment}
AND ( ai.full_name LIKE concat(concat('%',#{parameters.vague}),'%') OR ai.staff_number LIKE concat(concat('%',#{parameters.vague}),'%') )

mapper.update:
UPDATE
admin_info AS ai,department_admin AS da
SET
ai.the_department=#{theDepartment,jdbcType=VARCHAR},
ai.operator_id=#{operatorId,jdbcType=BIGINT},
da.d_id = #{did}
WHERE
ai.admin_id=#{adminId}
AND
da.admin_id = #{adminId}

mapper.select:
SELECT ai.admin_id,ai.full_name,ai.staff_number FROM admin_info ai WHERE ai.state = '1' AND ai.admin_id NOT IN (SELECT di.d_leader_id FROM department_info di)
AND ai.full_name LIKE concat(concat('%',#{parameter}),'%')
limit #{minRow},#{pageSize}
