mapper.select:
SELECT SQL_CALC_FOUND_ROWS
so.osaas_id, so.admin_id, so.user_id, so.user_uuid, so.user_nike, so.user_head, so.school_number, so.class_number, so.examination_number, so.register_number, so.full_name, so.gender, so.basic_id, so.period_id, so.grade_id,sg.grade_name, so.class_id, so.birth_date, so.card_type, so.card_number, so.photo_url, so.education_id, so.national_register_number, so.different_countries, so.hong_kong, so.registered_residence, so.registered_residence_detailed, so.political_outlook, so.nation, so.student_type, so.way_of_studying, so.treat_type, so.present_address, so.home_address, so.home_address_detailed, so.postal_address, so.native_heath, so.place_of_origin, so.health_condition, so.household_registration, so.mobile_phone, so.zip_code, so.anaphylaxis, so.past_medical_history, so.one_child, so.military_region, so.preschool_education, so.left_behind_children, so.work_for_children, so.name_pinyin, so.english_name, so.this_city_osaas, so.enrolment_category, so.enrolment_mode, so.student_source, so.date_of_admission, so.students_come_in, so.original_school, so.founder_id, so.create_time, so.operator_id, so.delete_time, so.state, so.zip_address, so.register_assist_number, so.class_year, so.belong_to_year, so.register_state, so.school_state, so.graduation_goto, so.learning_in_class, so.student_state, so.is_disability, so.domicile, so.orphan, so.martyr_child, so.is_subsidy, so.need_subsidy, so.remarks,sc.class_name FROM student_osaas so LEFT JOIN school_grade sg on so.grade_id = sg.grade_id left join school_class sc on so.class_id = sc.class_id
WHERE so.state = '1'
AND so.period_id = #{parameters.periodId}
AND so.belong_to_year = #{parameters.thetime}
AND so.class_id = #{parameters.classId}
-- 学生来处
AND so.students_come_in = #{parameters.isCity}
-- 原学校名称
AND so.original_school
= ( SELECT sb.school_name FROM school_basic sb WHERE sb.school_id = #{parameters.schoolId} )
AND ( so.full_name LIKE concat(concat('%',#{parameter}),'%') OR so.gender LIKE concat(concat('%',#{parameter}),'%') OR so.register_number LIKE concat(concat('%',#{parameter}),'%') )
limit #{minRow},#{pageSize}; SELECT FOUND_ROWS() AS total;
